sqlList.sql:
select ot.OUSER,nt.NUSER from
(
select count(LOGIN_MOBILE) AS OUSER from t_lm_user where CREATETIME >= TO_DATE(:StartDate || ' 04:00:00', 'yyyy-mm-dd hh24:mi:ss') and CREATETIME < TO_DATE(:EndDate || ' 04:00:00', 'yyyy-mm-dd hh24:mi:ss') and LOGIN_MOBILE IS NOT NULL
) ot, (
select count(id) AS NUSER from t_lm_user where CREATETIME >= TO_DATE(:StartDate || ' 04:00:00', 'yyyy-mm-dd hh24:mi:ss') and CREATETIME < TO_DATE(:EndDate || ' 04:00:00', 'yyyy-mm-dd hh24:mi:ss') and LOGIN_MOBILE IS NULL
) nt
